import styled from "styled-components";
import React from "react";
import Menu from "./Menu";
import { useEffect, useState } from "react";
import { redirect, useLocation } from "react-router-dom";
import Edit2 from "./Edit2";
import frame2 from '../images/frame2.svg'
import frame3 from '../images/frame3.svg'
import Header from "../components/Header";
import HTMLFlipBook from "react-pageflip";
import { useNavigate } from "react-router-dom";
import { onAuthStateChanged, getAuth, signInWithEmailAndPassword } from 'firebase/auth';
import {getStorage, ref, uploadBytes, listAll, getDownloadURL,} from "firebase/storage";
import { firestore ,firebaseApp,
  firebaseDb,
  firebaseStorage,} from '../firebase';

function PhotoBook() {
  let navigate = useNavigate();
    const location = useLocation();
    const albumName = location.state.albumName.albumName;
    // console.log(location.state.albumName.albumName);
    const [showMenu, setShowMenu] = useState(false);
    const [showEdit, setShowEdit] = useState(false);

    const themeList={
      nature:["url('./nature1.jpeg')", "url('./nature2.jpeg')", "url('./nature3.jpeg')", "url('./nature4.jpeg')"],
       cat:["url('./cat1.jpeg')", "url('./cat2.jpeg')", "url('./cat3.jpeg')"],
       pattern:["url('./pattern1.jpeg')", "url('./pattern2.jpeg')", "url('./pattern3.png')", "url('./pattern4.jpeg')"],
       black:["url('./black1.jpeg')", "url('./black2.jpeg')", "url('./black3.jpeg')", "url('./black4.jpeg')"],
       kitsch:["url('./kitsch1.jpeg')", "url('./kitsch2.png')", "url('./kitsch3.jpeg')", "url('./kitsch4.jpeg')"],
  }


    //유저 정보 확인해서 동일하고 이름도 동일한 앨범 가져오기
    //앨범의 테마 가져와서 설정
    const auth = getAuth();
    let userEmail =""
    let themeName=""
    const [forPropsThemeName, setForPropsThemeName] =useState("")
    const [theme, setTheme] =useState([])
    const [selectedImages, setSelectedImages] =useState([])
    const [selectedTheme,setSelectedTheme] = useState("")
    onAuthStateChanged(auth, (user) => {
      if (user) {
        userEmail = user.email
    const album = firestore.collection("album");
    album.doc(albumName+userEmail).get().then((document)=>{
      const albumInfo = document.data();
        themeName=albumInfo.theme
        setTheme(themeList[themeName])
        setSelectedTheme(themeName)
        setForPropsThemeName(albumInfo.theme)
        setSelectedImages(albumInfo.image)

})
      } else {
      }
    });

    const handleSelectedTheme=(e)=>{
      setSelectedTheme(e.target.value)
      // themeName=e.target.value;
  }

  const handleSelectedPhoto=(e)=>{
      let include = selectedImages.includes(e.target.value);
      if(!include){

          setSelectedImages(selectedImages.concat(e.target.value));
      }else{

          const filtered = selectedImages.filter((filter)=>filter!==e.target.value)
          setSelectedImages(filtered)
      }
  }



    const toshowMenu = ()=>{
        setShowMenu(true)
    }
    const tocloseMenu = ()=>{
      setShowMenu(false)
  }

  const toshowEdit=()=>{
    setShowEdit(true);
  }
  const tosave=()=>{
    console.log(albumName)
    console.log(userEmail)
    console.log(themeName)
    console.log(selectedImages)
    const album = firestore.collection("album")
    album.doc(albumName+userEmail).set(
      {
        title:albumName,
        creater:userEmail,
        theme:selectedTheme,
        image:selectedImages
      })
    setShowEdit(false);
    album.doc(albumName+userEmail).get().then((document)=>{
      const albumInfo = document.data();
  console.log(albumInfo)


      })

  }

  const Pages=(theme, selectedImages)=>{
     const result=[];
      for(let i=0; i< selectedImages.length; i++){
        result.push(<Page theme={theme[i%theme.length]}><Image src={selectedImages[i]}/></Page>)
      }
      return result;
  }
  // console.log(themeName)

  return (
    <Container>
        {showMenu?<Menu onclick={tocloseMenu}/>:""}
        {/* <Header starOnclick={toshowMenu} leftButton={showEdit?"save":"edit"} leftButtonOnclick={showEdit?tosave:toshowEdit} rightButton={showEdit?"delete":null}title={albumName}/> */}
        {showEdit?<Edit2 toshowMenu={toshowMenu} selectedPhoto={selectedImages} selectedTheme ={forPropsThemeName} albumName={albumName}/>:
        <>
        <Header starOnclick={toshowMenu} leftButton={"edit"} leftButtonOnclick={toshowEdit}title={albumName}/>
        <MainContainer>
                <HTMLFlipBook
                    width={700}
                    height={900} 
                    size="stretch"
                    minWidth={300}
                    // maxWidth={1500}
                    // minHeight={1000}
                    // maxHeight={1000}
                    maxShadowOpacity={0.3}
                    showCover={false}
                    mobileScrollSupport={true}
                    // onFlip={onPage}
                    className="demo-book">
                    {Pages(theme, selectedImages)}
                        {/* <Page theme="url('./theme1.svg')"><Image src={frame}/></Page>
                        <Page theme={theme1}><Image src={frame}/></Page>
                        <Page theme={theme1}><Image src={frame}/></Page>
                        <Page theme={theme1}><Image src={frame}/></Page> */}
                 </HTMLFlipBook>
        </MainContainer>
        </>}
    </Container>
  );
}

const Container = styled.div`
  font-family: goblin;
  min-height:100vh;
  display:flex;
  flex-direction:column;
  justify-content:center;
`
const MainContainer = styled.div`
    z-index:1;
    width:800px;
    max-height:800ppx;
    margin: auto;
    padding-top:130px;
    display:flex;
    // border:1px solid black;
    text-align:center;
    @media Screen and (max-width:1000px){
        width:600px;
        padding-top:180px;
        padding-bottom:140px;
    }
    @media Screen and (max-width:600px){
        width:0px;
        height100px;
        margin-left:calc(50vw - 150px);
        padding-top:140px;
        margin-top:auto;
        margin-bottom:auto;
    }
`
const Page = styled.div`
    min-width:400px;
    min-height:400px;
    background-image:${(props)=>props.theme};
    background-repeat: no-repeat;
    background-position: top center;
    background-size: cover;
    background-attachment: fixed;
    border:2px solid black;
    @media Screen and (max-width:1000px){
        min-width:270px;
        min-height:360px;
    }
    @media Screen and (max-width:600px){
        width:300px;
        padding-top:0px;
    }
`
const Image = styled.img`
  width:200px;
  height:450px;
  margin-top:30px;
  object-fil:cover;
  border:1px solid black;
  @media Screen and (max-width:1000px){
      width:150px;
      height:320px;
      margin-top:30px;
  }
  @media Screen and (max-width:600px){
    width:160px;
    padding-top:0px;
}
`
export default PhotoBook;
